from pdf2image import convert_from_pathimport osfrom PIL import Imageimport pytesseract as tesstess.pytesseract.tesseract_cmd = r'C:\Program Files\Tesseract-OCR\tesseract.exe'text_dir = './Texts/'def pdf_to_text_convert(pdf_file, image_dir):    if not os.path.exists(image_dir):        os.makedirs(image_dir)    pages = convert_from_path(pdf_file, 500)    image_counter = 1    for page in pages:        image_file_name = image_dir + 'page_' + str(image_counter) + '.jpg'        page.save(image_file_name, "JPEG")        image_counter = image_counter + 1        # print(file_name)    if not os.path.exists(text_dir):        os.makedirs(text_dir)    text_file = text_dir+'/text.txt'    f = open(text_file, "w", encoding='utf-8')    for i in range(1, image_counter):        image_file = image_dir+'page_'+str(i)+'.jpg'        text = tess.image_to_string(Image.open(image_file))        text = text.replace('-\n', '')        f.write(text)        # print(text)    f.close()    print('PDF to Text Conversion Done!')